import React, { useEffect, useState } from "react";
import { api } from "../helpers/api";

export default function MyListingsTab() {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        setLoading(true);
        const data = await api("/api/properties/mine/list"); // uses JWT cookie
        if (alive) setRows(data);
      } catch (e) {
        if (alive) setErr(e.message || "Failed to load");
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, []);

  if (loading) return <div>Loading your listings…</div>;
  if (err)      return <div style={{color:"#b91c1c"}}>{String(err)}</div>;
  if (!rows.length) return <div>You don’t have any listings yet.</div>;

  return (
    <div style={{ display:"grid", gridTemplateColumns:"repeat(auto-fill, minmax(260px, 1fr))", gap:16 }}>
      {rows.map(p => (
        <div key={p.id} style={{ border:"1px solid #e5e7eb", borderRadius:12, padding:12 }}>
          <img
            alt={p.title}
            src={p.imageUrl || (p.images?.[0] ? `/images/houses/${p.images[0]}` : "")}
            style={{ width:"100%", height:160, objectFit:"cover", borderRadius:8 }}
          />
          <h3 style={{ margin:"10px 0 4px" }}>{p.title}</h3>
          <div style={{ color:"#6b7280", fontSize:14 }}>
            {p.location || [p.city, p.state].filter(Boolean).join(", ")}
          </div>
          {p.price != null && (
            <div style={{ marginTop:8, fontWeight:600 }}>
              JOD {Number(p.price).toLocaleString("en-US")}
            </div>
          )}
          <a href={`/property/${p.id}`} style={{ display:"inline-block", marginTop:10, color:"#2563eb" }}>
            View
          </a>
        </div>
      ))}
    </div>
  );
}
