// frontend/src/pages/add-listing.js
import React, { useRef, useState } from "react";
import { useHistory } from "react-router-dom"; // v5
import { HeaderContainer, FooterContainer } from "../containers";
import { Section } from "../components";
import { api } from "../helpers/api";

const pageStyle  = { minHeight: "100vh", display: "flex", flexDirection: "column" };
const mainStyle  = { flex: 1 };
const formWrap   = { margin: "24px auto", maxWidth: 900 };
const grid       = { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 16 };
const row        = { display: "grid", gridTemplateColumns: "1fr", gap: 8, marginBottom: 12 };
const full       = { gridColumn: "1 / -1" };
const label      = { fontWeight: 600 };
const input      = { padding: 10, border: "1px solid #e5e7eb", borderRadius: 6 };
const textarea   = { ...input, minHeight: 110, resize: "vertical" };
const help       = { fontSize: 12, color: "#6b7280" };
const actions    = { display: "flex", gap: 12, marginTop: 16 };
const btn        = { padding: "10px 16px", borderRadius: 8, border: 0, cursor: "pointer" };
const primaryBtn = { ...btn, background: "#2563eb", color: "white" };
const dangerBtn  = { ...btn, background: "#ef4444", color: "white" };

// dropzone styles
const dz = {
  border: "2px dashed #93c5fd",
  borderRadius: 8,
  padding: 16,
  textAlign: "center",
  background: "#f8fafc",
};
const dzActive = { ...dz, borderColor: "#2563eb", background: "#eff6ff" };
const thumbs = { display: "grid", gridTemplateColumns: "repeat(auto-fill, 120px)", gap: 8, marginTop: 8 };
const thumb  = { position: "relative", width: 120, height: 90, overflow: "hidden", borderRadius: 8, background: "#eee" };
const thumbImg = { width: "100%", height: "100%", objectFit: "cover" };
const removeBtn = {
  position: "absolute", top: 4, right: 4, border: 0, borderRadius: 6,
  background: "rgba(0,0,0,0.55)", color: "#fff", padding: "2px 6px", cursor: "pointer", fontSize: 12,
};

export default function AddListingPage() {
  const history = useHistory();

  const [base, setBase] = useState({
    title: "",
    price: "",
    city: "",
    state: "",
    imageUrl: "",         // main image URL (or first uploaded)
    description: "",
    featured: false,
  });

  const [features, setFeatures] = useState({
    bedrooms: 0,
    bathrooms: 0,
    garage: 0,
    status: "Active",
    kitchen: "Available",
    elevator: "No",
  });

  const [address, setAddress] = useState({
    address: "",
    city: "",
    state: "",
    county: "",
    area: "",
    street: "",
    zip: "",
  });

  // gallery lists
  const [gallery, setGallery] = useState([]);   // uploaded/selected URLs
  const [imagesText, setImagesText] = useState(""); // optional manual URLs
  const [amenities, setAmenities] = useState("");

  const [saving, setSaving]   = useState(false);
  const [uploading, setUploading] = useState(false);
  const [error, setError]     = useState("");

  const mainFileInput = useRef(null);
  const galFileInput  = useRef(null);

  const parseList = (text) =>
    text.split(/[\n,]+/).map(s => s.trim()).filter(Boolean);

  // ------- upload helper (uses /api/uploads) -------
  async function uploadFiles(files) {
    const fd = new FormData();
    for (const f of files) fd.append("files", f);
    // use api() without setting Content-Type; browser sets it for FormData
    const res = await api("api/uploads", { method: "POST", body: fd, headers: {} });
    return res.urls || [];
  }

  // ------- Main image DnD -------
  const [mainOver, setMainOver] = useState(false);

  async function handleMainDrop(e) {
    e.preventDefault();
    setMainOver(false);
    setError("");
    const dt = e.dataTransfer;

    // If user dragged a URL
    const droppedUrl = dt.getData("text");
    if (droppedUrl && /^https?:\/\//i.test(droppedUrl.trim())) {
      setBase(b => ({ ...b, imageUrl: droppedUrl.trim() }));
      return;
    }

    // If user dragged files
    const files = dt.files && dt.files.length ? Array.from(dt.files) : [];
    if (!files.length) return;

    setUploading(true);
    try {
      const [url] = await uploadFiles(files.slice(0, 1));
      if (url) setBase(b => ({ ...b, imageUrl: url }));
    } catch (err) {
      setError(err.message || "Upload failed");
    } finally {
      setUploading(false);
    }
  }

  async function handleMainPick(e) {
    const files = Array.from(e.target.files || []);
    if (!files.length) return;
    setUploading(true);
    try {
      const [url] = await uploadFiles(files.slice(0, 1));
      if (url) setBase(b => ({ ...b, imageUrl: url }));
    } catch (err) {
      setError(err.message || "Upload failed");
    } finally {
      setUploading(false);
      e.target.value = "";
    }
  }

  // ------- Gallery DnD -------
  const [galOver, setGalOver] = useState(false);

  async function handleGalleryDrop(e) {
    e.preventDefault();
    setGalOver(false);
    setError("");
    const dt = e.dataTransfer;

    // URL dragged from another tab
    const urlFromText = dt.getData("text");
    if (urlFromText && /^https?:\/\//i.test(urlFromText.trim())) {
      setGallery(g => [...g, urlFromText.trim()]);
      return;
    }

    const files = dt.files && dt.files.length ? Array.from(dt.files) : [];
    if (!files.length) return;

    setUploading(true);
    try {
      const urls = await uploadFiles(files);
      setGallery(g => [...g, ...urls]);
    } catch (err) {
      setError(err.message || "Upload failed");
    } finally {
      setUploading(false);
    }
  }

  async function handleGalleryPick(e) {
    const files = Array.from(e.target.files || []);
    if (!files.length) return;
    setUploading(true);
    try {
      const urls = await uploadFiles(files);
      setGallery(g => [...g, ...urls]);
    } catch (err) {
      setError(err.message || "Upload failed");
    } finally {
      setUploading(false);
      e.target.value = "";
    }
  }

  function removeFromGallery(u) {
    setGallery(g => g.filter(x => x !== u));
  }

  // ------- Submit -------
  const onSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSaving(true);

    try {
      const payload = {
        ...base,
        // default main image to first gallery image if empty
        imageUrl: base.imageUrl || gallery[0] || "",
        price: Number(base.price || 0),
        images: [...gallery, ...parseList(imagesText)],
        amenities: parseList(amenities),
        features: {
          bedrooms: Number(features.bedrooms || 0),
          bathrooms: Number(features.bathrooms || 0),
          garage: Number(features.garage || 0),
          status: features.status || "Active",
          kitchen: features.kitchen || "Available",
          elevator: features.elevator || "No",
        },
        address: {
          ...address,
          city: address.city || base.city,
          state: address.state || base.state,
        },
      };

      const created = await api("/api/properties", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
        credentials: "include",
      });

      history.push(`/property/${created.id}`);
    } catch (err) {
      setError(err.message || "Failed to create property");
    } finally {
      setSaving(false);
    }
  };

  const reset = () => {
    setBase({ title: "", price: "", city: "", state: "", imageUrl: "", description: "", featured: false });
    setFeatures({ bedrooms: 0, bathrooms: 0, garage: 0, status: "Active", kitchen: "Available", elevator: "No" });
    setAddress({ address: "", city: "", state: "", county: "", area: "", street: "", zip: "" });
    setGallery([]);
    setImagesText("");
    setAmenities("");
    setError("");
  };

  return (
    <div style={pageStyle}>
      <HeaderContainer />

      <main style={mainStyle}>
        <Section style={formWrap}>
          <Section.InnerContainer>
            <Section.Title>Add Property</Section.Title>

            <form onSubmit={onSubmit}>
              <div style={grid}>
                {/* Base info */}
                <div style={row}>
                  <label style={label}>Title *</label>
                  <input style={input} value={base.title} onChange={e => setBase({ ...base, title: e.target.value })} required />
                </div>

                <div style={row}>
                  <label style={label}>Price</label>
                  <input type="number" min="0" step="1" style={input}
                    value={base.price}
                    onChange={e => setBase({ ...base, price: e.target.value })}
                  />
                </div>

                <div style={row}>
                  <label style={label}>City</label>
                  <input style={input} value={base.city} onChange={e => setBase({ ...base, city: e.target.value })} />
                </div>

                <div style={row}>
                  <label style={label}>State</label>
                  <input style={input} value={base.state} onChange={e => setBase({ ...base, state: e.target.value })} />
                </div>

                {/* MAIN IMAGE: drag & drop */}
                <div style={{ ...row, ...full }}>
                  <label style={label}>Main Image</label>

                  <div
                    style={mainOver ? dzActive : dz}
                    onDragOver={(e) => { e.preventDefault(); setMainOver(true); }}
                    onDragLeave={() => setMainOver(false)}
                    onDrop={handleMainDrop}
                    onClick={() => mainFileInput.current?.click()}
                    title="Click or drop an image here"
                  >
                    {base.imageUrl ? (
                      <img alt="Main" src={base.imageUrl} style={{ maxWidth: "100%", maxHeight: 240, objectFit: "contain" }} />
                    ) : (
                      <div>{uploading ? "Uploading..." : "Click or drag an image file/URL here"}</div>
                    )}
                  </div>
                  <input
                    ref={mainFileInput}
                    type="file"
                    accept="image/*"
                    style={{ display: "none" }}
                    onChange={handleMainPick}
                  />
                  <div style={help}>Drop a file or paste a direct image URL from the web.</div>
                </div>

                <div style={{ ...row, ...full }}>
                  <label style={label}>Description</label>
                  <textarea style={textarea} value={base.description} onChange={e => setBase({ ...base, description: e.target.value })} />
                </div>

                {/* GALLERY: drag & drop multiple */}
                <div style={{ ...row, ...full }}>
                  <label style={label}>Gallery</label>
                  <div
                    style={galOver ? dzActive : dz}
                    onDragOver={(e) => { e.preventDefault(); setGalOver(true); }}
                    onDragLeave={() => setGalOver(false)}
                    onDrop={handleGalleryDrop}
                    onClick={() => galFileInput.current?.click()}
                    title="Click or drop images here"
                  >
                    <div>{uploading ? "Uploading..." : "Click or drag multiple images/URLs here"}</div>
                    {gallery.length > 0 && (
                      <div style={thumbs}>
                        {gallery.map((u) => (
                          <div key={u} style={thumb}>
                            <img src={u} alt="" style={thumbImg} />
                            <button type="button" style={removeBtn} onClick={() => removeFromGallery(u)}>×</button>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                  <input
                    ref={galFileInput}
                    type="file"
                    multiple
                    accept="image/*"
                    style={{ display: "none" }}
                    onChange={handleGalleryPick}
                  />
                  <div style={help}>You can also add extra URLs below (comma/new line separated).</div>
                </div>

                {/* Optional extra URLs */}
                <div style={{ ...row, ...full }}>
                  <label style={label}>More Gallery URLs (optional)</label>
                  <textarea
                    style={textarea}
                    value={imagesText}
                    onChange={(e) => setImagesText(e.target.value)}
                    placeholder="/images/houses/a.jpg, /images/houses/b.jpg"
                  />
                </div>

                {/* Amenities */}
                <div style={{ ...row, ...full }}>
                  <label style={label}>Amenities</label>
                  <textarea
                    style={textarea}
                    value={amenities}
                    onChange={e => setAmenities(e.target.value)}
                    placeholder="Air Conditioning, Security System, Balcony, ..."
                  />
                </div>

                {/* Features */}
                <div style={row}>
                  <label style={label}>Bedrooms</label>
                  <input type="number" min="0" step="1" style={input}
                    value={features.bedrooms}
                    onChange={e => setFeatures({ ...features, bedrooms: e.target.value })}
                  />
                </div>
                <div style={row}>
                  <label style={label}>Bathrooms</label>
                  <input type="number" min="0" step="1" style={input}
                    value={features.bathrooms}
                    onChange={e => setFeatures({ ...features, bathrooms: e.target.value })}
                  />
                </div>
                <div style={row}>
                  <label style={label}>Garage</label>
                  <input type="number" min="0" step="1" style={input}
                    value={features.garage}
                    onChange={e => setFeatures({ ...features, garage: e.target.value })}
                  />
                </div>
                <div style={row}>
                  <label style={label}>Status</label>
                  <input style={input} value={features.status}
                    onChange={e => setFeatures({ ...features, status: e.target.value })}
                  />
                </div>
                <div style={row}>
                  <label style={label}>Kitchen</label>
                  <input style={input} value={features.kitchen}
                    onChange={e => setFeatures({ ...features, kitchen: e.target.value })}
                  />
                </div>
                <div style={row}>
                  <label style={label}>Elevator</label>
                  <input style={input} value={features.elevator}
                    onChange={e => setFeatures({ ...features, elevator: e.target.value })}
                  />
                </div>

                {/* Address */}
                <div style={{ ...row, ...full }}>
                  <label style={label}>Address (line)</label>
                  <input style={input} value={address.address} onChange={e => setAddress({ ...address, address: e.target.value })} />
                </div>
                <div style={row}>
                  <label style={label}>City</label>
                  <input style={input} value={address.city} onChange={e => setAddress({ ...address, city: e.target.value })} />
                </div>
                <div style={row}>
                  <label style={label}>State</label>
                  <input style={input} value={address.state} onChange={e => setAddress({ ...address, state: e.target.value })} />
                </div>
                <div style={row}>
                  <label style={label}>County/Sub-County</label>
                  <input style={input} value={address.county} onChange={e => setAddress({ ...address, county: e.target.value })} />
                </div>
                <div style={row}>
                  <label style={label}>Area</label>
                  <input style={input} value={address.area} onChange={e => setAddress({ ...address, area: e.target.value })} />
                </div>
                <div style={row}>
                  <label style={label}>Street</label>
                  <input style={input} value={address.street} onChange={e => setAddress({ ...address, street: e.target.value })} />
                </div>
                <div style={row}>
                  <label style={label}>ZIP</label>
                  <input style={input} value={address.zip} onChange={e => setAddress({ ...address, zip: e.target.value })} />
                </div>

                {/* Actions */}
                <div style={{ ...actions, ...full }}>
                  <button type="submit" style={primaryBtn} disabled={saving || uploading}>
                    {saving ? "Saving..." : uploading ? "Uploading..." : "Create Property"}
                  </button>
                  <button type="button" style={dangerBtn} onClick={reset}>Reset</button>
                </div>

                {error && (
                  <div style={{ ...full, color: "#b91c1c", marginTop: 4 }}>{error}</div>
                )}
              </div>
            </form>
          </Section.InnerContainer>
        </Section>
      </main>

      <FooterContainer />
    </div>
  );
}
