// frontend/src/partials/AddPropertySections.jsx
import React, { useRef, useState } from "react";
import { Add, Form } from "../components";

const Description = ({ form, onChange }) => {
  return (
    <Add.Description>
      <Add.DescriptionHeader>
        <Add.Title>Description</Add.Title>
      </Add.DescriptionHeader>
      <Add.DescriptionContent>
        <Add.DescriptionContentTop>
          <Form.FormGroup>
            <Form.Label>
              Property Title <span>(required)</span>
            </Form.Label>
            <Form.Input
              name="title"
              value={form.title || ""}
              onChange={onChange}
              required
            />
          </Form.FormGroup>

          <Form.FormGroup>
            <Form.Label>
              Property Price <span>(required)</span>
            </Form.Label>
            <Form.Input
              name="price"
              type="number"
              min="0"
              value={form.price || ""}
              onChange={onChange}
              required
            />
          </Form.FormGroup>

          <Form.FormGroup>
            <Form.Label>
              Category <span>(required)</span>
            </Form.Label>
            <Form.Select
              name="category"
              value={form.category || ""}
              onChange={onChange}
              required
            >
              <Form.Option value="">None</Form.Option>
              <Form.Option value="Apartment">Apartment</Form.Option>
              <Form.Option value="House">House</Form.Option>
              <Form.Option value="Land">Land</Form.Option>
            </Form.Select>
          </Form.FormGroup>

          <Form.FormGroup>
            <Form.Label>
              Listed In <span>(required)</span>
            </Form.Label>
            <Form.Select
              name="listedIn"
              value={form.listedIn || ""}
              onChange={onChange}
              required
            >
              <Form.Option value="">None</Form.Option>
              <Form.Option value="rental">Rental</Form.Option>
              <Form.Option value="sales">Sales</Form.Option>
            </Form.Select>
          </Form.FormGroup>
        </Add.DescriptionContentTop>

        <Add.DescriptionContentBottom>
          <Form.FormGroup>
            <Form.Label>
              Description <span>(required)</span>
            </Form.Label>
            <Form.TextArea
              name="description"
              rows="8"
              value={form.description || ""}
              onChange={onChange}
              required
            />
          </Form.FormGroup>
        </Add.DescriptionContentBottom>
      </Add.DescriptionContent>
    </Add.Description>
  );
};

const Location = ({ form, onChange }) => {
  return (
    <Add.Location>
      <Add.LocationHeader>
        <Add.Title>Property Location</Add.Title>
      </Add.LocationHeader>
      <Add.LocationContent>
        <Add.LocationContentTop>
          <Form.FormGroup>
            <Form.Label>
              Address <span>(required)</span>
